import React from 'react';
import axios from 'axios';

const API_key = "60a42d7c974241cab09bb73a40f9c403"

export default class Calendrier extends React.Component {

    constructor(props) {
        super(props);
        this.state =
        {
            city: "Paris",
            previous: "Paris",
            datetime: undefined

        }
        this.handleClick = this.handleClick.bind(this);
        this.handleChange = this.handleChange.bind(this);


    }

    componentDidMount() {
        axios.get(`https://timezone.abstractapi.com/v1/current_time`)
            .then(res => {
                this.setState(state => (
                    {
                        datetime: res.datetime
                    }
                ));
            })
    }



    handleClick() {
        axios.get(`https://timezone.abstractapi.com/v1/current_time?${API_key}&${this.state.city}`)
            .then(res => {
                this.setState(state => (
                    {
                        datetime: res.datetime
                    }
                ));
            })
    }

    handleChange(event) {
        this.setState({ city: event.target.value })
    }





    render() {

        return (
            <div className="meteo">
                <span className="itemsTitle">Calendrier</span>
                <div className="calendrierContainer">
                    <div className="dateCalendrier">
                        {this.state.datetime}
                    </div>
                    <input type="text" placeholder="Où es-tu ?" onChange={event => this.handleChange(event)} />
                    <button onClick={this.handleClick}>Avoir la météo</button>
                </div>
            </div>
        )
    }
}
