import React from "react";



const Weather = (props) => {
  return (
    <div className="container">
      <div className="cards">
      <h1 className="itemsTitle">Météo</h1>
      <h1>{props.city}</h1>
      <span className="meteoTemperature">{props.celsius}°C</span> 
      {minmaxTemp(2,5)}
      <h1>
          {props.weatherIcon},
          {props.description}
      </h1>
      </div>
    </div>
  )
}

function minmaxTemp(min,max) {
  return(
    <><span className="minTemp">{min}°C</span><span className="maxTemp">{max}°C</span></>
  );
}

export default Weather;
