    render() {
        return(
            <div><Weather city={this.state.city} 
            celsius={this.state.celsius} 
            temp_min={this.state.temp_min} 
            temp_max={this.state.temp_max} 
            description={this.state.description}/></div>

        );
    }
}

export default Meteo;

