import React from 'react'
import "./Meteo.css"
import "./API_Weather"


export default function Meteo() {
    return (

        <div class="meteo">
            <span className="itemsTitle">Météo</span>
            <div className="meteoContainer">
            <p id="day0-forecast-more-info"></p>
            <div id="day0-icon-weather-container"></div>
            <h3 id="day0-forecast-temp"> </h3>
                {/* <span className="meteoLocation">Paris</span><br />
                <span className="meteoTemperature">5°C</span> */}
            </div>
        </div>)

}

// Fonction appelée lors du click du bouton
function start() {
    // Création de l'objet apiWeather
    const apiWeather = new API_WEATHER();
    // Appel de la fonction fetchTodayForecast

    apiWeather
      .fetchThreeDaysForecast()
      .then(function (response) {
        // Récupère la donnée d'une API
        const data = response.data;

        // On récupère l'information principal
        const main = data.weather[0].main;
        const description = data.weather[0].description;
        const temp = data.main.temp;
        const icon = apiWeather.getHTMLElementFromIcon(data.weather[0].icon);

        // Modifier le DOM
        document.getElementById('today-forecast-main').innerHTML = main;
        document.getElementById('today-forecast-more-info').innerHTML = description;
        document.getElementById('icon-weather-container').innerHTML = icon;
        document.getElementById('today-forecast-temp').innerHTML = `${temp}°C`;

      })
      .catch(function (error) {
        // Affiche une erreur
        console.error(error);
      });
  }
