import React from 'react';
import axios from 'axios';

const API_key = "923b8b8bfbca71d0369fd0505d99dcf4"

export default class WeatherWidget extends React.Component {

  constructor(props) {
    super(props);
    this.state =
    {
      city: "Paris",
      previous: "Paris",
      icon: `https://risibank.fr/cache/stickers/d579/57932-full.gif`,
      main: "",
      temp: "",
      temp_max: "",
      temp_min: "",
      description: ""

    }
    this.handleClick = this.handleClick.bind(this);
    this.handleChange = this.handleChange.bind(this);


  }

  componentDidMount() {
    axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${this.state.city}&units=metric&appid=${API_key}`)
      .then(res => {
        this.setState(state => (
          {
            URL: `http://openweathermap.org/img/wn/${res.data.weather[0].icon}@2x.png`,
            main: res.data.weather[0].main,
            descritpion: res.data.weather[0].descritpion,
            temp: res.data.main.temp
          }
        ));
      })
  }



  handleClick() {
    axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${this.state.city}&units=metric&appid=923b8b8bfbca71d0369fd0505d99dcf4`)
      .then(res => {
        this.setState(state => (
          {
            URL: `http://openweathermap.org/img/wn/${res.data.weather[0].icon}@2x.png`,
            main: res.data.weather[0].main,
            descritpion: res.data.weather[0].descritpion,
            temp: res.data.main.temp,
            previous: this.state.city
          }
        ));
      })
  }

  handleChange(event) {
    this.setState({ city: event.target.value })
  }





  render() {

    return (
      <div className="meteo">
          <span className="itemsTitle">Météo</span>
          <div className="city-name">{this.state.previous}</div>
          <img src={this.state.URL} alt="forecast" />
          <div className="main-forecast">{this.state.main}</div>
          <div className="description-forecast">{this.state.descritpion}</div>
          <div className="meteoTemperature">{this.state.temp}°C</div>
          <input type="text" value={this.state.city} onChange={event => this.handleChange(event)} />
          <button onClick={this.handleClick}>Clique ici!!!</button>
      </div>
    )
  }
}

